mapper.select:
SELECT

FROM grow_basic_batch_task


        and mod(id,#{condition.taskItemNum}) in (${condition.containIds})


        AND code = #{condition.code}


        AND name = #{condition.name}


        AND taskType = #{condition.taskType}


        AND bizCode = #{condition.bizCode}


        AND (startTime is null or startTime <= #{condition.startTime})


        AND dealStatus = #{condition.dealStatus}


        AND dealTime = #{condition.dealTime}


        AND dealDesc = #{condition.dealDesc}


        AND creator = #{condition.creator}


        AND lastModifier = #{condition.lastModifier}


        AND status = #{condition.status}



    ORDER BY ${orderBy} ${sortBy}


    limit ${offset}, ${rows}
